// import { deepblue, navyblue, teal} from './DisplayPage.js'
import '../Day.css'

function Day({ image1, image2, image3, date, txtColor}) {
    let img3 = null;
    if(image3){
        img3 = <img src={image3} alt="image3" className='image3' id='selec-pic'></img>
    }

    $(document).ready(function () {
        // Attach click event to elements with the class "date-title"
        $('.date-title').on('click', function () {
            // Toggle the visibility of the associated "body-container"
            const detailsContainer = $(this).siblings('.body-container');
            detailsContainer.toggle();
        })}
    )
    return(
        <div className='day-container'>
        <h2 className='bdate-txt' style={{ color: txtColor }}>April { date }</h2>
        <div className='selection'>
            <img src={image1} alt="image1" className='image1' id='selec-pic'></img>
            <img src={image2} alt="image2" className='image2' id='selec-pic'></img>
            { img3 }
        </div>
        <div className='date-title'>
            <p>this is a test</p>
        </div>
    </div>
    )
}

export default Day
