// import { deepblue, navyblue, teal} from './DisplayPage.js'
import '../Day.css'
import React, {useState} from 'react'

function Day({ image1, image2, image3, date, txtColor, schedule }) {
    let img3 = null;
    const [toggle, setToggle] = useState(false)
    const [vanish, setVanish] = useState(true)

    if(image3){
        img3 = <img src={image3} alt="image3" className='image3' id='selec-pic'></img>
    }

    if(!schedule){
        return <div>Loading...</div>
    }


    return(
        <div className='day-container'>
        {vanish && 
            <><h2 className='bdate-txt' style={{ color: txtColor }}>April {date}</h2><div className='selection'
                        onClick={() => setToggle(!toggle)}>
                        <img src={image1} alt="image1" className='image1' id='selec-pic'></img>
                        <img src={image2} alt="image2" className='image2' id='selec-pic'></img>
                        {img3}
                    </div></>
        }
        <div className='info-container' 
            onClick={() => setToggle(!toggle) && setVanish(!vanish)}>
            {toggle &&  
            (
                // <p>test</p>
                <p>{ schedule.title }</p>
            )}
        </div>
    </div>
    )
}

export default Day
